    public async Task GivenUpworkRssFeedFetcher_WhenPersonRssFeedIsSet_ThenFeedIsFetchedAndJobCommandsArePublished(
        string rawFeedResponse, int expectedJobCommandCount)
    {
        // Arrange
        var fakeHttpClient = new FakeHttpClient(rawFeedResponse);
        var fakeCommandPublisher = new FakeCommandPublisher();
        var upworkRssFeedFetcher = new UpworkRssFeedFetcher(
            _testDataBuilder.PersonQueryRepository,
            new NullLogger<UpworkRssFeedFetcher>(),
            new FakeClock(),
            fakeCommandPublisher,
            fakeHttpClient);
        
        // Act
        await upworkRssFeedFetcher.StartFetchingAllAsync(CancellationToken.None);
        
        // Assert
        Assert.That(fakeCommandPublisher.PublishedCommands.Count, Is.EqualTo(expectedJobCommandCount),
            "Unexpected number of job commands published");
    }
